package java_1;
import java.io.*;
import java.util.*;

public class Sort {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int array[]= {100,55,34,22,134,76,31};
for(int no:array)
{System.out.print("Number="+" "+no+'\n');
	}
Arrays.sort(array);
for(int no:array)
{System.out.println("Sorted array="+no);
	}
	}

}
